CREATE TABLE IF NOT EXISTS `yy_cms_category` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `pid` bigint(20) unsigned DEFAULT '0' COMMENT '上级ID',
  `mid` varchar(20) DEFAULT '' COMMENT '所属模型',
  `addons` varchar(255) DEFAULT NULL COMMENT '关联插件',
  `catname` varchar(100) DEFAULT '' COMMENT '栏目名称',
  `icon` varchar(100) DEFAULT '' COMMENT '菜单图标',
  `catdir` varchar(100) DEFAULT '' COMMENT '栏目目录',
  `parentdir` varchar(255) DEFAULT NULL COMMENT '父级目录',
  `type` varchar(10) DEFAULT 'content' COMMENT '栏目类型',
  `arrparentid` varchar(255) DEFAULT NULL COMMENT '父级ID',
  `arrchildid` varchar(255) DEFAULT NULL COMMENT '子类ID',
  `url` varchar(400) DEFAULT '' COMMENT '外部链接',
  `content` longtext COMMENT '栏目内容',
  `tpl_page` varchar(255) DEFAULT NULL COMMENT '单页模板',
  `tpl_show` varchar(50) DEFAULT '' COMMENT '详情页模板',
  `tpl_list` varchar(50) DEFAULT '' COMMENT '列表页模板',
  `tpl_search` varchar(50) DEFAULT NULL COMMENT '搜索页模板',
  `tpl_category` varchar(50) DEFAULT NULL COMMENT '封面页模板',
  `target` varchar(20) DEFAULT '_self' COMMENT '打开方式',
  `pagesize` int(10) unsigned DEFAULT '10' COMMENT 'PC端每页数量',
  `mpagesize` int(10) unsigned DEFAULT '10' COMMENT '手机端列表数量',
  `sort` int(11) unsigned DEFAULT '0' COMMENT '排序权重',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '状态(0:禁用,1:启用)',
  `create_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `ishtml` tinyint(3) unsigned DEFAULT '0' COMMENT '生成静态',
  `seo_title` varchar(200) DEFAULT NULL COMMENT 'SEO标题',
  `seo_keyword` varchar(220) DEFAULT NULL COMMENT 'SEO关键字',
  `seo_description` varchar(300) DEFAULT NULL COMMENT 'SEO描述',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `idx_system_menu_node` (`catdir`) USING BTREE,
  KEY `idx_system_menu_status` (`status`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='系统-菜单';

