CREATE TABLE IF NOT EXISTS `yy_cms_content` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `cid` bigint(20) unsigned DEFAULT NULL COMMENT '所属栏目',
  `extend_id` varchar(255) DEFAULT NULL COMMENT '扩展分类ID',
  `title` varchar(50) DEFAULT '' COMMENT '素材标题',
  `cover_pic` varchar(100) DEFAULT '' COMMENT '封面图片',
  `author` varchar(20) DEFAULT '' COMMENT '文章作者',
  `keyword` varchar(255) DEFAULT NULL COMMENT '关键字',
  `digest` varchar(300) DEFAULT '' COMMENT '摘要内容',
  `source` varchar(50) DEFAULT '''本站''' COMMENT '来源',
  `source_url` varchar(200) DEFAULT '' COMMENT '来源地址',
  `read_num` bigint(20) unsigned DEFAULT '0' COMMENT '阅读数量',
  `create_by` bigint(20) unsigned DEFAULT '1' COMMENT '创建人',
  `create_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `sort` bigint(20) unsigned DEFAULT '100' COMMENT '排序',
  `status` tinyint(3) unsigned DEFAULT '0' COMMENT '状态(0未发布，1已发布)',
  `is_deleted` tinyint(3) unsigned DEFAULT '0' COMMENT '删除状态(0未删除,1已删除)',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='微信-文章';

