CREATE TABLE IF NOT EXISTS `yy_cms_content_data` (
  `id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '文章ID',
  `content` longtext COMMENT '图文内容',
  `audit_text` varchar(255) DEFAULT NULL COMMENT '审核意见',
  `audit_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '审核时间',
  `addons` text COMMENT '关联插件',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='微信-文章';

-- ----------------------------
-- Records of yy_cms_content_data
