-- Table structure for yy_core_department
-- ----------------------------

CREATE TABLE IF NOT EXISTS `yy_core_department` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID主键',
  `pid` bigint(20) unsigned DEFAULT '0' COMMENT '上级单位',
  `sort` bigint(20) unsigned DEFAULT '0' COMMENT '排序',
  `status` tinyint(4) unsigned DEFAULT '1' COMMENT '状态',
  `name` varchar(40) DEFAULT '' COMMENT '部门名称',
  `address` varchar(255) DEFAULT '' COMMENT '办公地址',
  `tel` varchar(15) DEFAULT '' COMMENT '办公电话',
  `zcode` varchar(20) DEFAULT '' COMMENT '省份',
  `create_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `desc` mediumtext CHARACTER SET utf8mb4 COMMENT '部门描述',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='单位部门';

-- ----------------------------
-- Table structure for yy_core_region
-- ----------------------------

CREATE TABLE IF NOT EXISTS `yy_core_region` (
  `id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `pid` smallint(5) unsigned DEFAULT '0' COMMENT '父级ID',
  `zcode` varchar(20) DEFAULT '' COMMENT '行政区划代码',
  `name` varchar(100) DEFAULT '0',
  `type` tinyint(1) unsigned DEFAULT '2',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '状态',
  `sort` int(10) unsigned DEFAULT '0' COMMENT '排序',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='地区表';

-- ----------------------------
-- Table structure for yy_system_aclog
-- ----------------------------

CREATE TABLE IF NOT EXISTS `yy_system_aclog` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `node` varchar(200) NOT NULL DEFAULT '' COMMENT '当前操作节点',
  `geoip` varchar(15) NOT NULL DEFAULT '' COMMENT '操作者IP地址',
  `action` varchar(200) NOT NULL DEFAULT '' COMMENT '操作行为名称',
  `content` varchar(1024) NOT NULL DEFAULT '' COMMENT '操作内容描述',
  `username` varchar(50) NOT NULL DEFAULT '' COMMENT '操作人用户名',
  `create_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='系统-操作日志';

